        {/* Start Assessment Button */}
        <button
          onClick={() => navigate('/problem')}
          className="w-full bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600 transition-colors text-lg mt-6"
        >
          Start Assessment
        </button>
      </div>
    </div>
  );
}
