import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const questions = [
  { text: "Do you have at least one close friend?", feature: 'close.friend' },
  { text: "Do you engage in repetitive behaviors?", feature: 'repetitive.behaviour' },
  // Add all 22 questions matching your ML model features
];

export default function problem() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [age, setAge] = useState('');
  const navigate = useNavigate();

  const handleAnswer = (answer) => {
    const feature = questions[currentQuestion].feature;
    const newAnswers = {
      ...answers,
      [feature]: feature === 'Age' ? parseInt(answer) : answer
    };

    setAnswers(newAnswers);
    
    if(currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
    } else {
      submitAnswers(newAnswers);
    }
  };

  const submitAnswers = async (answers) => {
    try {
      const response = await axios.post('http://localhost:5000/api/predict', { answers });
      navigate('/results', { state: response.data });
    } catch (error) {
      console.error('Submission error:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-sm p-6">
        {/* Progress bar */}
        <div className="mb-4 h-2 bg-gray-200 rounded">
          <div 
            className="h-full bg-blue-500 rounded transition-all duration-300"
            style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
          ></div>
        </div>

        <h2 className="text-2xl font-semibold text-blue-600 mb-6">
          Question {currentQuestion + 1}/{questions.length}
        </h2>

        <p className="text-gray-700 mb-8 text-lg">
          {questions[currentQuestion].text}
        </p>

        {questions[currentQuestion].feature === 'Age' ? (
          <div className="space-y-4">
            <input
              type="number"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              className="w-full p-2 border rounded mb-4"
              placeholder="Enter your age"
            />
            <button
              onClick={() => handleAnswer(age)}
              className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
            >
              Next
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            <button 
              onClick={() => handleAnswer('yes')}
              className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
            >
              Yes
            </button>
            <button
              onClick={() => handleAnswer('no')}
              className="w-full bg-gray-200 text-gray-800 py-2 rounded hover:bg-gray-300"
            >
              No
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
