import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useState } from 'react';

export default function Home() {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [showProfile, setShowProfile] = useState(false);

  return (
    <div className="min-h-screen bg-blue-50 flex items-center justify-center p-4 relative">
      {/* Profile Section */}
      <div className="absolute top-5 right-5">
        <div className="relative">
          <button 
            onClick={() => setShowProfile(!showProfile)} 
            className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center focus:outline-none"
          >
            üë§
          </button>
          
          {showProfile && (
            <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-lg p-4">
              <p className="text-gray-700 mb-2 text-sm">{user?.email}</p>
              <button 
                onClick={logout} 
                className="w-full text-red-500 hover:underline text-sm text-left"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-8 text-center">
        {/* App Title */}
        <h1 className="text-3xl font-bold text-blue-600">MindWell</h1>

        {/* Mood Selection */}
        <p className="text-gray-700 mb-2">How are you feeling today?</p>
        <div className="flex justify-center space-x-4">
          <button onClick={() => navigate(`/assessment?mood=happy`)} className="text-2xl">üòä</button>
          <button onClick={() => navigate(`/assessment?mood=neutral`)} className="text-2xl">üòê</button>
          <button onClick={() => navigate(`/assessment?mood=sad`)} className="text-2xl">üò¢</button>
        </div>
      </div>
    </div>
  );
}
