import { useLocation, useNavigate } from 'react-router-dom';

export default function Results() {
  const { state } = useLocation();
  const navigate = useNavigate();

  // Handle case where user navigates directly to results page
  if (!state) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <div className="text-center max-w-md bg-white rounded-xl p-8 shadow-lg">
          <h2 className="text-2xl font-bold text-red-500 mb-4">
            No Results Found
          </h2>
          <p className="text-gray-600 mb-6">
            Please complete the mental health assessment first.
          </p>
          <button
            onClick={() => navigate('/questionnaire')}
            className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
          >
            Take Assessment
          </button>
        </div>
      </div>
    );
  }

  const { diagnosis, confidence, recommendations } = state;

  // Default recommendations if none provided
  const defaultRecommendations = [
    'Consult a licensed mental health professional',
    'Practice mindfulness meditation daily',
    'Maintain a regular sleep schedule',
    'Engage in physical activity regularly'
  ];

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-lg p-6">
        <h1 className="text-3xl font-bold text-blue-600 mb-6 text-center">
          Assessment Results
        </h1>

        {/* Diagnosis Section */}
        <div className="bg-blue-50 p-6 rounded-lg mb-6">
          <h2 className="text-xl font-semibold text-blue-800 mb-2">
            Diagnosis
          </h2>
          <p className="text-gray-700 text-lg">
            {diagnosis || 'No specific disorder detected'}
          </p>
          <div className="mt-4">
            <span className="text-sm font-medium text-blue-600">
              Confidence Level: {(confidence * 100).toFixed(1)}%
            </span>
          </div>
        </div>

        {/* Recommendations Section */}
        <div className="bg-green-50 p-6 rounded-lg mb-6">
          <h3 className="text-lg font-semibold text-green-800 mb-3">
            Recommended Actions
          </h3>
          <ul className="list-disc pl-5 space-y-2">
            {(recommendations || defaultRecommendations).map((item, index) => (
              <li key={index} className="text-gray-700">
                {item}
              </li>
            ))}
          </ul>
        </div>

        {/* Navigation Buttons */}
        <div className="flex gap-4 mt-8">
          <button
            onClick={() => navigate('/home')}
            className="flex-1 bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition-colors"
          >
            Back to Home
          </button>
          <button
            onClick={() => navigate('/problem')}
            className="flex-1 bg-gray-200 text-gray-800 py-2 rounded-lg hover:bg-gray-300 transition-colors"
          >
            Retake Assessment
          </button>
        </div>
      </div>
    </div>
  );
}
