import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useState } from 'react';

export default function Home() {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [darkMode, setDarkMode] = useState(false);
  const [showProfile, setShowProfile] = useState(false);

  return (
    <div className={`${darkMode ? 'bg-gray-900 text-white' : 'bg-blue-50 text-gray-900'} min-h-screen flex items-center justify-center p-4 relative`}>
      {/* Dark Mode Toggle */}
      <button 
        onClick={() => setDarkMode(!darkMode)} 
        className="absolute top-5 left-5 p-2 bg-gray-300 dark:bg-gray-700 rounded-full text-sm"
      >
        {darkMode ? 'üåô Dark' : '‚òÄÔ∏è Light'}
      </button>

      {/* Profile Section at the Top Right */}
      <div className="absolute top-5 right-5">
        <div className="relative">
          {/* Profile Image Button */}
          <button 
            onClick={() => setShowProfile(!showProfile)} 
            className="w-10 h-10 bg-gray-300 dark:bg-gray-600 rounded-full flex items-center justify-center focus:outline-none"
          >
            üë§
          </button>
          
          {/* Profile Dropdown */}
          {showProfile && (
            <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 shadow-lg rounded-lg p-4">
              <p className="text-gray-700 dark:text-gray-300 mb-2 text-sm">{user?.email}</p>
              <button 
                onClick={logout} 
                className="w-full text-red-500 hover:underline text-sm text-left"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="w-full max-w-md bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 text-center">
        {/* App Title */}
        <h1 className="text-3xl font-bold text-blue-600 dark:text-blue-400">MindWell</h1>

        {/* Welcome Message */}
        <p className="text-gray-700 dark:text-gray-300 mb-4">
          Welcome to MindWell. Please click below to start your assessment.
        </p>

        {/* Mood Selection */}
        <p className="text-gray-700 dark:text-gray-300 mb-2">How are you feeling today?</p>
        <div className="flex justify-center mb-4 space-x-4">
          <button onClick={() => navigate('/problem?mood=happy')} className="text-2xl">üòä</button>
          <button onClick={() => navigate('/problem?mood=neutral')} className="text-2xl">üòê</button>
          <button onClick={() => navigate('/problem?mood=sad')} className="text-2xl">üò¢</button>
        </div>

        {/* Start Assessment Button */}
        <button
          onClick={() => navigate('/problem')}
          className="w-full bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600 transition-colors text-lg"
        >
          Start Assessment
        </button>
      </div>
    </div>
  );
}
